import React from "react";
import { Button } from 'react-bootstrap';
import useFetch from "./hooks/useFetch";

const GithubDetail = () => {
  const data = useFetch("https://api.github.com/users/ashleynd");
  console.log(data.response)
  if (data.isLoading) {
    return <div>Loading...</div>;
  }
  if (data.error) {
    return <div>Sorry, something went wrong :(</div>
  }
  const { avatar_url, login, name, followers, following, location, public_repos } = data.response;
  return (
    <div className="App">
      <div>
        <h3>Username: {login}</h3>
        <h4>Name: {name}</h4>
        <div>
          <img src={avatar_url} alt="avatar" width="150px" />
        </div>
        <p>{location}</p>
        <p>Followers: {followers}</p>
        <p>Following: {following}</p>
        <p>Public Repositories: {public_repos}</p>
        <a href="https://api.github.com/users/ashleynd/repos" target="_blank">
          <Button variant="primary"> Repositories </Button>
        </a>
      </div>
    </div>
  );
};

export default GithubDetail;
